package Iterations;


public class Pattern6_Q12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int no=6,n=1;
		
		
		for(int i=0;i<no;i++){
				for(int j=0;j<=i;j++){
					System.out.print(n+ " ");
					n++;
				}
		System.out.println();
		}
	}

}
